/**
 * Define Game Types:
 */

type Machine = "machine";
type Human = "human";
type Player = Machine | Human;
type Tile = Player | null;
type Board = Tile[][];
type GameResult = Player | "tie";

/**
 * Create Player and Game classes:
 */


interface TicTacToePlayer {
    play: (board: Board) => Board;
}

class GamePlayer implements TicTacToePlayer {
    player: Player;

    constructor(player: Player) {
        this.player = player;
    }

    play(board: Board): Board {
        for (let i = 0; i < board.length; i++) {
            for (let j = 0; j < board.length; j++) {
                const tile = board[i][j];
                if (tile === null) {
                    board[i][j] = this.player;
                    return board;
                }
            }
        }
    }
}

interface TicTacToeGame {
    board: Board;
    checkBoard: (board: Board) => GameResult | null;
    playGame: (humanPlayer: TicTacToePlayer, machinePlayer: TicTacToePlayer) => GameResult;
}

class Game implements TicTacToeGame {
    board: Board;
    constructor(board: Board) {
        this.board = board;
    }

    checkBoard(board: Board): GameResult | null {
        const countNulls = (board: Board) => {
            const nulls = board.reduce((nullCount, row) => row.reduce((c, x) => x === null ? c + 1 : c, 0), 0);
            if (nulls === 0) {
                return "tie!";
            }
        }

        const checkRows = (rowIndex: number = 0) => {
            const [left, middle, right] = board[rowIndex];
            if (left === middle && middle === right) {
                if (left !== null) {
                    return left;
                } else if (rowIndex < board.length - 1) {
                    return checkRows(rowIndex + 1);
                } else {
                    return null;
                }
            }
        }

        const checkColumns = (columnIndex: number = 0) => {
            const top = board[0][columnIndex];
            const middle = board[1][columnIndex];
            const bottom = board[2][columnIndex];

            if (top === middle && middle === bottom) {
                if (top !== null) {
                    return top;
                } else if (columnIndex < board[0].length - 1) {
                    return checkColumns(columnIndex + 1);
                } else {
                    return null;
                }
            }
        }

        let winner = null;

        // Check rows
        winner = checkRows();
        if (winner) {
            return winner;
        }

        // Check columns
        winner = checkColumns();
        if (winner) {
            return winner;
        }

        // Get coordinates for diagonal tiles
        const topLeft = board[0][0];
        const topRight = board[0][2];
        const middle = board[0][0];
        const bottomLeft = board[2][0];
        const bottomRight = board[2][2];

        // Check top left to bottom right diagonal
        if (topLeft === middle && middle === bottomLeft) {
            if (topLeft !== null) {
                return topLeft;
            }
        }

        // Check bottom left to top right diagonal
        if (bottomLeft === middle && middle === topRight) {
            if (bottomLeft !== null) {
                return bottomLeft;
            }
        }

        return null;
    }

    turn(player: TicTacToePlayer, nextPlayer: TicTacToePlayer): GameResult {
        player.play(this.board);
        const winner = this.checkBoard(this.board);
        if (winner !== null) {
            return winner;
        } else {
            return this.turn(nextPlayer, player);
        }
    }

    playGame(humanPlayer: TicTacToePlayer, machinePlayer: TicTacToePlayer): GameResult {
        return this.turn(humanPlayer, machinePlayer);
    }

    printBoard() {
        for (const row of this.board) {
            console.log(row);
        }
    }
}

/**
 * Instantiate game pieces:
 */

const board = [
    [null, null, null],
    [null, null, null],
    [null, null, null],
];

const human = new GamePlayer("human");
const machine = new GamePlayer("machine");
const game = new Game(board);

/**
 * Play the game1
 */

const winner = game.playGame(human, machine);
game.printBoard();

