interface Person {
    name: string;
    age: number;
}

const comparePeopleReferences = (referenceOne: Person, referenceTwo: Person) => {
    return referenceOne === referenceTwo;
}

const comparePeople = (personOne: Person, personTwo: Person) => {
    const agesEqual = personOne.age === personTwo.age;
    const namesEqual = personOne.name === personTwo.name;
    return agesEqual && namesEqual;
}

const Rob: Person = {
    name: "Rob",
    age: 23,
}

const RobTwin: Person = {
    name: "Rob",
    age: 23,
}

const areRobsReferencesTheSame = comparePeopleReferences(Rob, RobTwin);
const areRobsTheSame = comparePeople(Rob, RobTwin);

console.log(`Are Rob references the same? -> ${areRobsReferencesTheSame}`);
console.log(`Are Robs the same? -> ${areRobsTheSame}`);

