challenges.solutionCode:
import React from "react";
import ReactDOM from "react-dom";

interface IState {
  value: string;
}

class App extends React.Component<{}, IState> {
  constructor(props: {}) {
    super(props);

    this.state = {
      value: "",
    };
  }

  render(): JSX.Element {
    const welcome: string = "Hello, React!";
    console.log("Hello from the iframe!!!");
    return (
      <div>
        <h1>{welcome}</h1>
        <input value={this.state.value} onChange={this.handleChange} />
      </div>
    );
  }

  handleChange = (e: any) => {
    this.setState({ value: e.target.value });
  };
}

// Do not edit code below this line
const Main = App;
ReactDOM.render(<Main />, document.querySelector("#root"));

challenges.testCode:
test("Renders a <h1> tag with the text 'Hello, React!'", () => {
  const container = document.createElement("div");
  ReactTestUtils.act(() => {
    ReactDOM.render(<Main />, container);
  });
  const label = container.querySelector("h1");
  return label.textContent === "Hello, React!";
});

test("Renders a controlled <input /> using React state", () => {
  const container = document.createElement("div");
  ReactTestUtils.act(() => {
    ReactDOM.render(<Main />, container);
  });
  const inputEl = container.querySelector("input");
  const testValue = "giraffe";
  ReactTestUtils.Simulate.change(inputEl, { target: { value: testValue } });
  return inputEl.value === testValue;
});
