 * This is a multi-line comment.
 *
 * The following code demonstrates various types of comment
 * usage. For regular comments you have /* ... */ multiline
 * comments like we are in now, and also single-line comments,
 * e.g. // This is my comment.
 *
 * Rust also supports standard documentation comments, which
 * are indicated using a triple slash: ///
 *
 * These documentation comments are displayed in the HTML
 * documentation generated when running 'cargo doc'. For
 * more on this take a look here:
 * https://doc.rust-lang.org/rust-by-example/meta/doc.html
 */

/// Enum representing different possible weapon types
#[derive(Debug)]
enum WeaponTypes {
  Sword,
  Spear,
  Dagger,
  BowAndArrow,
  Mace,
  Axe,
}

/// A struct for a package item in a game
#[derive(Debug)]
struct Weapon {
  /// The weapon type
  weapon_type: WeaponTypes,
}

impl Weapon {
  /// Create a new weapon, given a weapon type
  pub fn new(weapon_type: WeaponTypes) -> Weapon {
    Weapon {
      weapon_type,
    }
  }
}

/**
 * This is a function to create some new weapons.
 */
fn create_three_weapons() -> (Weapon, Weapon, Weapon) {
  println!("\nCreating 3 new weapons...\n");

  // Create a sword
  let sword = Weapon::new(WeaponTypes::Sword);
  println!("Created a sword: {:?}", sword);

  // Create a mace
  let mace = Weapon::new(WeaponTypes::Mace);
  println!("Created a mace: {:?}", mace);

  // Create a sword
  let dagger = Weapon::new(WeaponTypes::Dagger);
  println!("Created a dagger: {:?}", dagger);

  println!("\nFinished weapon creation!");

  // Return a tuple of the three created weapons
  return (sword, mace, dagger);
}

// Create some weapons
create_three_weapons();

