// Determine if a string is a valid substring of another
fn string_sub_string(a: &str, b: &str) -> bool {
  // Immediately return true if strings are simply equal, including empty strings
  if a == b {
    return true;
  }

  // Convert both strings to vectors
  let mut a_vec = Vec::new();
  let mut b_vec = Vec::new();

  for c in a.chars() {
    a_vec.push(c);
  }

  for c in b.chars() {
    b_vec.push(c);
  }

  // Ensure the first is longer than the second
  if b_vec.len() > a_vec.len() {
    return false;
  }

  // Iterate through the first string vector until the first character
  // matches the first character of the second. If it does, try to
  // iterate forward from there and match all adjacent characters.
  let mut index = 0;
  while index < a_vec.len() {
    let a_value = a_vec[index];
    let b_value = b_vec[0];

    if a_value == b_value {
      let mut a_index = index + 1;
      let mut b_index = 1;
      let mut valid = true;

      while b_index < b_vec.len() {
        if a_vec[a_index] == b_vec[b_index] {
          a_index = a_index + 1;
          b_index = b_index + 1;
        } else {
          valid = false;
          break;
        }
      }

      if valid {
        return true;
      }
    }
    index = index + 1;
  }

  // If you reached here and found no valid match, there is none.
  return false;
}

