  sixteen_bit_unsigned: u16,
  thirty_two_bit_signed: i32,
  thirty_two_bit_unsigned: u32,
  sity_four_bit_signed: i64,
  sity_four_bit_unsigned: u64,
  one_hundred_twenty_eight_bit_signed: i128,
  one_hundred_twenty_eight_bit_unsigned: u128,
}

fn get_all_integer_data_types() -> IntegerTypes {
  // Update code below this line:

  // 8-bit
  let eight_bit_signed = -4;
  let eight_bit_unsigned = 4;

  // 16-bit
  let sixteen_bit_signed = -500;
  let sixteen_bit_unsigned = 500;

  // 32-bit
  let thirty_two_bit_signed = -10000;
  let thirty_two_bit_unsigned = 10000;

  // 64-bit
  let sity_four_bit_signed = -10000000;
  let sity_four_bit_unsigned = 10000000;

  // 128-bit
  let one_hundred_twenty_eight_bit_signed = -10000000000;
  let one_hundred_twenty_eight_bit_unsigned = 10000000000;

  // Update code above this line:

  return IntegerTypes {
    eight_bit_signed,
    eight_bit_unsigned,
