fn find_in_vector(v: Vec<i64>, target: i64) -> Option<u32> {
  let mut found_count: u32 = 0;

  for x in v.iter() {
    if *x == target {
      found_count = found_count + 1;
    }
  }

  if found_count == 0 {
    None
  } else {
    Some(found_count)
  }
}

