struct Rectangle {
  width: u32,
  height: u32,
}

impl Rectangle {
  fn new(width: u32, height: u32) -> Self {
    Rectangle {
      width,
      height,
    }
  }

  fn area(&self) -> u32 {
    self.width * self.height
  }
}

